mapper.select:
select
CarId, s_id, DocumentNumber, Documentdate, Documentstatus, carmagid, Phone, Amountreceivable, Deliverystatus,
Completionstatus, e_id, remarks, p_id
from asms.carwaslist


        and CarId = #{carid}


        and s_id = #{sId}


        and DocumentNumber = #{documentnumber}


        and Documentdate = #{documentdate}


        and Documentstatus = #{documentstatus}


        and carmagid = #{carmagid}


        and Phone = #{phone}


        and Amountreceivable = #{amountreceivable}


        and Deliverystatus = #{deliverystatus}


        and Completionstatus = #{completionstatus}


        and e_id = #{eId}


        and remarks = #{remarks}


        and p_id = #{pId}
