mapper.select:
select
  p_id, Servetypeid, Siteid, s_id, Projectcode, ProjectName, State, Remarks, Type_Label, TypeId, WorkHours, CustAccountPrcie, InsidePrice, ClaimantPrcie
from asms.projectsettings


        and p_id = #{pId}


        and Servetypeid = #{servetypeid}


        and Siteid = #{siteid}


        and s_id = #{sId}


        and Projectcode = #{projectcode}


        and ProjectName = #{projectname}


        and State = #{state}


        and Remarks = #{remarks}


        and Type_Label = #{typeLabel}


        and TypeId = #{typeid}


        and WorkHours = #{workhours}


        and CustAccountPrcie = #{custaccountprcie}


        and InsidePrice = #{insideprice}


        and ClaimantPrcie = #{claimantprcie}
