mapper.select:
select
r_id, materialcode, materialname, typename, brand, modelname, sitepartname, unitname, numberd, backnumber,
unitprice, amountprice, storename, billcode
from asms.w_returnedmaterials


        and r_id = #{rId}


        and materialcode = #{materialcode}


        and materialname = #{materialname}


        and typename = #{typename}


        and brand = #{brand}


        and modelname = #{modelname}


        and sitepartname = #{sitepartname}


        and unitname = #{unitname}


        and numberd = #{numberd}


        and backnumber = #{backnumber}


        and unitprice = #{unitprice}


        and amountprice = #{amountprice}


        and storename = #{storename}


        and billcode = #{billcode}
